CREATE OR REPLACE FUNCTION obter_pr_imposto ( cd_tributo_p bigint, cd_estabelecimento_p bigint default null, dt_vigencia_p timestamp default null) RETURNS bigint AS $body$
DECLARE


retorno_w		bigint;


BEGIN
	
	select	max(pr_imposto)
	into STRICT	retorno_w
	from	regra_calculo_imposto e
	where	e.cd_tributo			          = cd_tributo_p
	and 	((coalesce(e.cd_estabelecimento::text, '') = '') or (coalesce(cd_estabelecimento_p::text, '') = '') or (cd_estabelecimento_p = e.cd_estabelecimento))
	and     coalesce( trunc(dt_vigencia_p), trunc( e.dt_vigencia))		>=  	trunc(e.dt_vigencia)
	and ( coalesce( trunc(dt_vigencia_p),e.dt_vigencia) between trunc(e.dt_vigencia) and trunc(e.dt_fim_vigencia) or coalesce(dt_fim_vigencia::text, '') = '');


return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
