CREATE OR REPLACE FUNCTION obter_pr_prev_ativ_crono ( dt_inicio_crono_p timestamp, --Data prevista de inicio do cronograma
 dt_fim_crono_p timestamp, -- Data prevista de fim do cronograma
 dt_inicio_prev_ativ_p timestamp  --Data prevista de inicio de uma atividade do cronograma
 ) RETURNS bigint AS $body$
 /*
	O objetivo desta function é retornar a porcentagem prevista de um cronograma em um determinado período entre  dt_inicio_prev_ativ_p e dt_fim_prev_ativ_p.
*/
DECLARE


pr_retorno_w      double precision;
pr_por_dia_w      double precision;
qt_dias_uteis_w   bigint;
qt_dias_trab_w    bigint;


BEGIN

	select	OBTER_DIAS_UTEIS_PERIODO(dt_inicio_crono_p, dt_fim_crono_p, 1)
	into STRICT	qt_dias_uteis_w
	;

	select	dividir(100, qt_dias_uteis_w)
	into STRICT	pr_por_dia_w
	;

	select	OBTER_DIAS_UTEIS_PERIODO(dt_inicio_crono_p, dt_inicio_prev_ativ_p, 1)
	into STRICT	qt_dias_trab_w
	;

	pr_retorno_w	:= round((pr_por_dia_w * qt_dias_trab_w)::numeric, 1);

	if (pr_retorno_w > 100) then
		pr_retorno_w	:= 100;
	end if;

return	pr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
