CREATE OR REPLACE FUNCTION obter_pr_prev_crono ( dt_inicio_p timestamp, dt_fim_p timestamp ) RETURNS bigint AS $body$
DECLARE


pr_retorno_w      double precision;
pr_por_dia_w      double precision;
qt_dias_uteis_w   bigint;
qt_dias_trab_w    bigint;


BEGIN

	select	OBTER_DIAS_UTEIS_PERIODO(dt_inicio_p, dt_fim_p, 1)
	into STRICT	qt_dias_uteis_w
	;


	select	dividir(100, qt_dias_uteis_w)
	into STRICT	pr_por_dia_w
	;

	select	OBTER_DIAS_UTEIS_PERIODO(dt_inicio_p, clock_timestamp(), 1)
	into STRICT	qt_dias_trab_w
	;

	pr_retorno_w	:= (pr_por_dia_w * qt_dias_trab_w);

	if (pr_retorno_w > 100) then
		pr_retorno_w	:= 100;
	end if;

RETURN	pr_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
