CREATE OR REPLACE FUNCTION obter_pr_real_portifolio ( nr_seq_portifolio_p bigint ) RETURNS bigint AS $body$
DECLARE

/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  Obter percentual real de execução de um portifolio
---------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ]  Relatórios [ ] Outros:
 --------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
pr_retorno_w double precision := 0;


BEGIN

  select ( select dividir(t.per_exec * 100,t.qt_hora_prev) pr_realizado
           from ( select sum(a.qt_hora_prev) qt_hora_prev,
                           sum(dividir(a.qt_hora_prev * a.pr_etapa,100)) per_exec
                    from   proj_projeto_inf_v a
                    where  a.nr_seq_portifolio = nr_seq_portifolio_p
                  ) t
         ) pr_real
  into STRICT   pr_retorno_w
;

  return  pr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_pr_real_portifolio ( nr_seq_portifolio_p bigint ) FROM PUBLIC;

