SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_pt_nas_atend ( nr_atendimento_p bigint, dt_data_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100);
dt_data_w		varchar(10);



BEGIN

if (length(dt_data_p) < 10) then
	dt_data_w := substr('0' || dt_data_p,1,10);
else
	dt_data_w := substr(dt_data_p,1,10);
end if;


select	qt_pontuacao
into STRICT	ds_retorno_w
from	escala_nas a
where	a.dt_avaliacao = ( 	SELECT	max(e.dt_avaliacao)
				from	escala_nas e
				where	e.nr_atendimento = nr_atendimento_p
				and	to_char(e.dt_avaliacao,'dd/mm/yyyy') = dt_data_w);


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
