CREATE OR REPLACE FUNCTION obter_qdade_real_div (cd_material_p bigint, nr_prescricao_p bigint, nr_seq_material_P bigint) RETURNS bigint AS $body$
DECLARE



qt_quantidade_w bigint := 0;


BEGIN

if ((coalesce(nr_prescricao_p,0) > 0) and (coalesce(cd_material_p,0) > 0)) then

SELECT 		SUBSTR(obter_qt_mat_original(a.nr_prescricao,a.nr_sequencia),1,100)
into STRICT        		qt_quantidade_w
FROM prescr_material a
LEFT OUTER JOIN material_atend_paciente b ON (a.nr_prescricao = b.nr_prescricao AND a.NR_SEQUENCIA = b.nr_sequencia_Prescricao)
LEFT OUTER JOIN prescr_material_devolucao c ON (a.nr_prescricao = c.nr_prescricao AND a.nr_sequencia = c.nr_seq_prescricao)
WHERE a.nr_prescricao = nr_prescricao_p and a.cd_material = cd_material_p and a.nr_sequencia = nr_seq_material_p;

end if;

return	qt_quantidade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
