CREATE OR REPLACE FUNCTION obter_qtdade_devolvida_barras ( nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


qt_devolvido_w		double precision;
qt_material_w		double precision;
cd_motivo_baixa_w	smallint;
dt_baixa_w		timestamp;
qt_original_w		double precision;
qt_atendido_w		double precision;


BEGIN

select	coalesce(max(qt_material),0),
	max(cd_motivo_baixa),
	max(dt_baixa),
	coalesce(max(qt_original),0),
	coalesce(max(qt_atendido),0)
into STRICT	qt_material_w,
	cd_motivo_baixa_w,
	dt_baixa_w,
	qt_original_w,
	qt_atendido_w
from	prescr_material
where	nr_prescricao 	= nr_prescricao_p
and	nr_sequencia	= nr_sequencia_p;

if (cd_motivo_baixa_w <> 0) and (dt_baixa_w IS NOT NULL AND dt_baixa_w::text <> '') then
	qt_devolvido_w := qt_original_w - qt_atendido_w;
elsif (qt_material_w = 0) and (qt_original_w > 0) then
	qt_devolvido_w := qt_original_w - qt_atendido_w;
else
	qt_devolvido_w := 0;
end if;

return	qt_devolvido_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
