CREATE OR REPLACE FUNCTION obter_qtdade_emprestimo ( cd_local_estoque_p bigint, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


qt_emprestimo_w		double precision;


BEGIN

if (cd_local_estoque_p IS NOT NULL AND cd_local_estoque_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	begin
	select	/*+ INDEX(b empmate_i1) index (c emprest_pk) */		coalesce(sum(CASE WHEN c.ie_tipo='S' THEN  qt_material * -1  ELSE qt_material END ),0)
	into STRICT	qt_emprestimo_w
	from	emprestimo c,
		emprestimo_material b
	where	c.cd_local_estoque	= cd_local_estoque_p
	and	b.nr_emprestimo	= c.nr_emprestimo
	and	b.qt_material	> 0
	and ((coalesce(c.ie_origem,'D') <> 'P') or ((c.dt_aprovacao IS NOT NULL AND c.dt_aprovacao::text <> '') and coalesce(c.dt_reprovacao::text, '') = '' and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')))
	and	exists (	SELECT	1
			from	material a
			where	a.cd_material_estoque = cd_material_p
			and	a.cd_material = b.cd_material);
	end;
end if;

return	qt_emprestimo_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
