CREATE OR REPLACE FUNCTION obter_qtdade_kit_material ( cd_kit_p bigint, cd_material_p bigint, cd_medico_p text) RETURNS bigint AS $body$
DECLARE


ds_retorno_w		double precision;
cd_estabelecimento_w	smallint;


BEGIN
select 	wheb_usuario_pck.get_cd_estabelecimento
into STRICT	cd_estabelecimento_w
;

select	sum(qt_material)
into STRICT	ds_retorno_w
from	componente_kit
where	cd_kit_material = cd_kit_p
and	((cd_material	= cd_material_p) or coalesce(cd_material_p::text, '') = '')
and	((cd_medico = cd_medico_p) or coalesce(cd_medico_p::text, '') = '')
and	((coalesce(cd_estab_regra::text, '') = '') or (cd_estab_regra = cd_estabelecimento_w));

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
