CREATE OR REPLACE FUNCTION obter_qtde_mat_kit_modif ( nr_prescricao_p bigint, nr_seq_recomendacao_p bigint, cd_recomendacao_p bigint, cd_material_p bigint, cd_kit_p bigint, nr_seq_rec_cpoe_p bigint) RETURNS bigint AS $body$
DECLARE


qt_material_w		double precision;


BEGIN

select	coalesce(max(qt_material),0)
into STRICT	qt_material_w
from	w_kit_modificado
where	((nr_prescricao = nr_prescricao_p) or
		 ((coalesce(nr_prescricao::text, '') = '') and (nr_seq_rec_cpoe = nr_seq_rec_cpoe_p)))
and	cd_recomendacao = cd_recomendacao_p
and	cd_material = cd_material_p
and	cd_kit = cd_kit_p;

return	qt_material_w;

end;
$body$
