CREATE OR REPLACE FUNCTION obter_qtde_menor_minimo_ressup (cd_material_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


cd_material_estoque_w			bigint;
qt_estoque_minimo_w			double precision;
qt_Estoque_w				double precision;
qt_faltante_w				double precision := 0;


BEGIN

SELECT	a.cd_material_estoque,
	b.qt_estoque_minimo
INTO STRICT	cd_material_estoque_w,
	qt_estoque_minimo_w
FROM	material a,
	material_estab b
WHERE	a.cd_material = b.cd_material
AND	b.cd_Estabelecimento = cd_estabelecimento_p
AND	a.cd_material = cd_material_p;

SELECT	SUM(qt_estoque)
INTO STRICT	qt_estoque_w
FROM	saldo_estoque
WHERE	cd_estabelecimento = cd_estabelecimento_p
AND	cd_material = cd_material_estoque_w
AND	dt_mesano_referencia = TRUNC(clock_timestamp(),'mm');

if (qt_estoque_w < qt_Estoque_minimo_w) then
	qt_faltante_W := qt_Estoque_minimo_w - qt_estoque_w;
end if;

return	qt_faltante_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
