CREATE OR REPLACE FUNCTION obter_qtde_pendente_prescr_mat ( nr_atendimento_p bigint, nr_prescricao_p bigint, nr_seq_prescricao_p bigint, nr_devolucao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(1) := 'S';
qt_material_w			material_atend_paciente.qt_material%type;
qt_material_devol_w		material_atend_paciente.qt_material%type;
cd_convenio_parametro_w		convenio.cd_convenio%type;
cd_categoria_parametro_w		categoria_convenio.cd_categoria%type;
cd_setor_atendimento_w		setor_atendimento.cd_setor_atendimento%type;
dt_recebimento_w			timestamp;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;
cd_plano_convenio_w		varchar(10);
cd_material_prescricao_w		material.cd_material%type;
cd_material_conta_w		material.cd_material%type;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescricao_p IS NOT NULL AND nr_seq_prescricao_p::text <> '') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_devolucao_p IS NOT NULL AND nr_devolucao_p::text <> '') then
	
	select	coalesce(sum(a.qt_material),0)
	into STRICT	qt_material_w
	from	material_atend_paciente a
	where	a.nr_atendimento = nr_atendimento_p
	and	a.nr_prescricao = nr_prescricao_p
	and	a.nr_sequencia_prescricao = nr_seq_prescricao_p;
	
	select	coalesce(sum(qt_material),0)
	into STRICT	qt_material_devol_w
	from	item_devolucao_material_pac
	where	nr_devolucao = nr_devolucao_p
	and	nr_prescricao = nr_prescricao_p
	and	nr_sequencia_prescricao = nr_seq_prescricao_p;
	
	ie_retorno_w := 'N';
	
	if (qt_material_w > qt_material_devol_w) then
		ie_retorno_w := 'S';
	end if;
	
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
