CREATE OR REPLACE FUNCTION obter_qtde_regra_preco_farcom (cd_material_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		bigint;
cd_grupo_w		integer;
cd_subgrupo_w		integer;
cd_classe_w		bigint;


BEGIN

select	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material
into STRICT	cd_grupo_w,
	cd_subgrupo_w,
	cd_classe_w
from	estrutura_material_v
where	cd_material = cd_material_p;

select	count(*)
into STRICT	vl_retorno_w
from	far_regra_preco_mat
where	cd_estabelecimento = cd_estabelecimento_p
and	((coalesce(cd_material,0) = cd_material_p) or (coalesce(cd_material,0) = 0))
and	((coalesce(cd_grupo_material,0) = cd_grupo_w) or (coalesce(cd_grupo_material,0) = 0))
and	((coalesce(cd_subgrupo_material,0) = cd_subgrupo_w) or (coalesce(cd_subgrupo_material,0) = 0))
and	((coalesce(cd_classe_material,0) = cd_classe_w) or (coalesce(cd_classe_material,0) = 0));

return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
