SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qtd_atend_episodio (nr_seq_episodio_p bigint, nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

	
    qtd_encounters_w  smallint := 0;


BEGIN

    SELECT  COUNT(*) qtd_encounters
    INTO STRICT    qtd_encounters_w
    FROM    atendimento_paciente
    WHERE   nr_seq_episodio = nr_seq_episodio_p
    AND     nr_atendimento <> nr_atendimento_p
    AND     coalesce(dt_cancelamento::text, '') = '';

    RETURN qtd_encounters_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
