CREATE OR REPLACE FUNCTION obter_qtd_componente_kit ( cd_kit_material_p bigint, nr_seq_kit_estoque_p bigint, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


qt_material_w	double precision;


BEGIN

if (coalesce(cd_kit_material_p,0) > 0) and (coalesce(cd_material_p,0) > 0) then
	select	max(qt_material)
	into STRICT	qt_material_w
	from	componente_kit
	where	cd_kit_material 	= cd_kit_material_p
	and	cd_material		= cd_material_p
	and	coalesce(ie_situacao,'A') 	= 'A';
end if;

if (coalesce(nr_seq_kit_estoque_p,0) > 0) and (coalesce(cd_material_p,0) > 0) then
	select	max(qt_material)
	into STRICT	qt_material_w
	from	kit_estoque_comp
	where	nr_seq_kit_estoque 	= nr_seq_kit_estoque_p
	and	cd_material		= cd_material_p;
end if;

return	qt_material_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
