CREATE OR REPLACE FUNCTION obter_qtd_contas_protocolo (nr_protocolo_p bigint) RETURNS bigint AS $body$
DECLARE

 
qtd_contas	bigint;		
		   

BEGIN 
 
SELECT	coalesce(COUNT(*),0) 
into STRICT	qtd_contas 
FROM 	pessoa_fisica c, 
	atendimento_paciente b, 
	conta_paciente a 
WHERE 	a.nr_atendimento    = b.nr_atendimento 
AND 	b.cd_pessoa_fisica   = c.cd_pessoa_fisica 
AND 	a.nr_seq_protocolo   = nr_protocolo_p;
 
 
return	qtd_contas;
 
end;
