SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qtd_convertida_sem_round ( cd_material_p bigint, qt_material_p bigint, cd_unidade_medida_p text, cd_unidade_medida_retorno_p text, ie_converter_retorno_p text default 'N') RETURNS bigint AS $body$
DECLARE


/*Opções cd_unidade_medida_retorno_p:
UME = Unidade Medida Estoque
UMC = Unidade Medida Consumo
UMP = Unidade Medida Compra*/
cd_unidade_medida_compra_w	varchar(30);
cd_unidade_medida_estoque_w	varchar(30);
cd_unidade_medida_consumo_w	varchar(30);
qt_conv_compra_estoque_w	double precision;
qt_conv_estoque_consumo_w	double precision;
qt_retorno_w			double precision;
qt_conversao_w			double precision;
qt_existe_conversao_w		bigint;
cd_estabelecimento_w		smallint := wheb_usuario_pck.get_cd_estabelecimento;


BEGIN
select	substr(obter_dados_material_estab(cd_material,cd_estabelecimento_w,'UMC'),1,255) cd_unidade_medida_compra,
	substr(obter_dados_material_estab(cd_material,cd_estabelecimento_w,'UME'),1,30) cd_unidade_medida_estoque,
	substr(obter_dados_material_estab(cd_material,cd_estabelecimento_w,'UMS'),1,30) cd_unidade_medida_consumo,
	qt_conv_compra_estoque,
	qt_conv_estoque_consumo
into STRICT	cd_unidade_medida_compra_w,
	cd_unidade_medida_estoque_w,
	cd_unidade_medida_consumo_w,
	qt_conv_compra_estoque_w,
	qt_conv_estoque_consumo_w
from 	material
where	cd_material = cd_material_p;

select	count(*)
into STRICT	qt_existe_conversao_w
from	material_conversao_unidade
where	cd_material = cd_material_p
and	cd_unidade_medida = cd_unidade_medida_p;

/*Converte primeiro para quantidade de estoque*/

if (cd_unidade_medida_p = cd_unidade_medida_estoque_w) then
	qt_retorno_w := qt_material_p;
elsif (cd_unidade_medida_p = cd_unidade_medida_consumo_w) then
	if (ie_converter_retorno_p = 'S') then
		qt_retorno_w := qt_material_p * qt_conv_estoque_consumo_w;
	else
		qt_retorno_w := DIVIDIR_SEM_ROUND(qt_material_p,qt_conv_estoque_consumo_w);
	end if;
elsif (cd_unidade_medida_p = cd_unidade_medida_compra_w) then
	if (ie_converter_retorno_p = 'S') then
		qt_retorno_w := DIVIDIR_SEM_ROUND(qt_material_p,qt_conv_compra_estoque_w);
	else
		qt_retorno_w := qt_material_p * qt_conv_compra_estoque_w;
	end if;
elsif (qt_existe_conversao_w > 0) then
	begin
	select	coalesce(max(qt_conversao),0)
	into STRICT	qt_conversao_w
	from	material_conversao_unidade
	where	cd_material = cd_material_p
	and	cd_unidade_medida = cd_unidade_medida_p;

	if (qt_conversao_w > 0) then
		begin
		if (ie_converter_retorno_p = 'S') then
			qt_retorno_w := qt_material_p * qt_conversao_w;
			qt_retorno_w := qt_retorno_w * qt_conv_estoque_consumo_w;
		else
			qt_retorno_w := qt_material_p/qt_conversao_w;
			qt_retorno_w := qt_retorno_w/qt_conv_estoque_consumo_w;
		end if;
		end;
	else
		qt_retorno_w := qt_material_p;
	end if;
	end;
else
	begin
	select	coalesce(max(qt_conversao),0)
	into STRICT	qt_conversao_w
	from	unidade_medida_adic_compra
	where	cd_material = cd_material_p
	and	cd_unidade_medida = cd_unidade_medida_p;

	if (qt_conversao_w > 0) then
		begin
		if (ie_converter_retorno_p = 'S') then
			qt_retorno_w := qt_material_p / qt_conversao_w;
		else
			qt_retorno_w := qt_material_p * qt_conversao_w;
		end if;
		end;
	else
		qt_retorno_w := qt_material_p;
	end if;
	end;
end if;

/*Converte a quantidade para a unidade de medida desejada, se for  de estoque não converte, pois já está convertida*/

if (cd_unidade_medida_retorno_p = 'UMC') then
	if (ie_converter_retorno_p = 'S') then
		qt_retorno_w := DIVIDIR_SEM_ROUND(qt_retorno_w,qt_conv_estoque_consumo_w);
	else
		qt_retorno_w := qt_retorno_w * qt_conv_estoque_consumo_w;
	end if;
elsif (cd_unidade_medida_retorno_p = 'UMP') then
	if (ie_converter_retorno_p = 'S') then
		qt_retorno_w := qt_retorno_w * qt_conv_compra_estoque_w;
	else
		qt_retorno_w := DIVIDIR_SEM_ROUND(qt_retorno_w,qt_conv_compra_estoque_w);
	end if;
end if;

return	qt_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_qtd_convertida_sem_round ( cd_material_p bigint, qt_material_p bigint, cd_unidade_medida_p text, cd_unidade_medida_retorno_p text, ie_converter_retorno_p text default 'N') FROM PUBLIC;

