CREATE OR REPLACE FUNCTION obter_qtd_devolvida (nr_prescricao_p bigint, cd_material_p bigint, nr_seq_lote_fornec_p bigint) RETURNS bigint AS $body$
DECLARE



qt_material_w	bigint := 0;


BEGIN

SELECT SUM(a.qt_devolucao)
into STRICT	qt_material_w
FROM   prescr_material_devolucao a,
	   prescr_material b
WHERE  a.nr_seq_prescricao 	 = b.nr_sequencia
AND	   a.nr_prescricao	   	 = b.nr_prescricao
AND	   b.nr_prescricao	   	 = nr_prescricao_p
AND	   b.cd_material	   	 = cd_material_p
AND    coalesce(nr_seq_lote_fornec,coalesce(nr_seq_lote_fornec_p,0)) = coalesce(nr_seq_lote_fornec_p,0);

return	qt_material_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
