CREATE OR REPLACE FUNCTION obter_qtd_dias_aut_convenio (cd_convenio_p bigint, ie_tipo_p text, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE

					 
qt_tempo_autorizacao_w	bigint:=0;					
 

BEGIN 
 
 
if (coalesce(cd_convenio_p,0) > 0) then 
	if (ie_tipo_p = 'M') then 
	 
		select	coalesce(max(qt_dias_autorizacao),0) 
		into STRICT	qt_tempo_autorizacao_w 
		from	convenio_estabelecimento 
		where	cd_convenio = cd_convenio_p 
		and	 cd_estabelecimento = cd_estabelecimento_p;
 
		if (coalesce(qt_tempo_autorizacao_w,0) = 0) then 
			select	coalesce(max(qt_dias_autorizacao),0) 
			into STRICT	qt_tempo_autorizacao_w 
			from	convenio 
			where	cd_convenio = cd_convenio_p;
		end if;
	elsif (ie_tipo_p = 'P') then 
		select	coalesce(max(qt_dias_autor_proc),0) 
		into STRICT	qt_tempo_autorizacao_w 
		from	convenio_estabelecimento 
		where	cd_convenio = cd_convenio_p 
		and	 cd_estabelecimento = cd_estabelecimento_p;
		 
		if (coalesce(qt_tempo_autorizacao_w,0) = 0) then 
			select	coalesce(max(qt_dias_autor_proc),0) 
			into STRICT	qt_tempo_autorizacao_w 
			from	convenio 
			where	cd_convenio = cd_convenio_p;
		end if;
	elsif (ie_tipo_p = 'C') then 
		select	coalesce(max(qt_dias_codificacao),0) 
		into STRICT	qt_tempo_autorizacao_w 
		from	convenio 
		where	cd_convenio = cd_convenio_p;
	elsif (ie_tipo_p = 'T') then 
		select	coalesce(max(qt_dias_term_agenda),0) 
		into STRICT	qt_tempo_autorizacao_w 
		from	convenio 
		where	cd_convenio = cd_convenio_p;	
	end if;	
end if;	
 
return 	qt_tempo_autorizacao_w;
 
end;
$body$
