CREATE OR REPLACE FUNCTION obter_qtd_doacoes_ano ( cd_pessoa_fisica_p text, nr_seq_doacao_p bigint) RETURNS bigint AS $body$
DECLARE


qtd_w	integer;
ie_considera_doacao_w	varchar(1);
ie_doacao_w		varchar(1);

BEGIN

select ie_contar_doacao
into STRICT	ie_considera_doacao_w
from 	san_parametro
where 	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

if (ie_considera_doacao_w = 'S') then

	select	count(*) + 1
	into STRICT	qtd_w
	from   	san_doacao
	where  	ie_avaliacao_final = 'A'
	and	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	nr_sequencia <> nr_seq_doacao_p
	and	dt_doacao between(clock_timestamp() - interval '365 days') and clock_timestamp();
else
	begin

	select 	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ie_doacao_w
	from	san_doacao
	where	nr_sequencia = nr_seq_doacao_p
	and 	ie_status in (1,5);

	if (ie_doacao_w = 'S') then
		select	count(*)
		into STRICT	qtd_w
		from   	san_doacao
		where  	ie_avaliacao_final = 'A'
		and	cd_pessoa_fisica = cd_pessoa_fisica_p
		and	nr_sequencia <> nr_seq_doacao_p
		and	dt_doacao between(clock_timestamp() - interval '365 days') and clock_timestamp();
	else
		select	count(*)  + 1
		into STRICT	qtd_w
		from   	san_doacao
		where  	ie_avaliacao_final = 'A'
		and	cd_pessoa_fisica = cd_pessoa_fisica_p
		and	nr_sequencia <> nr_seq_doacao_p
		and	dt_doacao between(clock_timestamp() - interval '365 days') and clock_timestamp();
	end if;
	end;
end if;

return	qtd_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
