CREATE OR REPLACE FUNCTION obter_qtd_exame_acesso ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE

/**
	Retorna a qtd de exames lidos via web
	Juliane Menin - 15/10/2007
*/
qtd_retorno_w		varchar(20);

BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	select count(*) qtd
	into STRICT qtd_retorno_w
        from    result_laboratorio a
        where   nr_prescricao  = nr_prescricao_p
        and '2'    =  (SELECT d.ie_status
                        FROM   result_laboratorio_acesso d
                        WHERE  d.nr_seq_result_lab = a.nr_sequencia
                        AND	   d.nr_sequencia = (SELECT MAX(f.nr_sequencia)
                                FROM 	result_laboratorio_acesso f
                                WHERE  f.nr_seq_result_lab = d.nr_seq_result_lab));
end if;
RETURN qtd_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
