CREATE OR REPLACE FUNCTION obter_qtd_exame_laudo_lib ( nr_prescricao_p bigint, nr_sequencia_p bigint, nr_seq_exame_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w	bigint;



BEGIN
if (coalesce(nr_seq_exame_p::text, '') = '')then
	select	count(*)
	into STRICT	qt_retorno_w
	from	laudo_paciente
	where	nr_prescricao		= nr_prescricao_p
	and	nr_seq_prescricao	= nr_sequencia_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

else
	SELECT 	coalesce(max(1),0)
	into STRICT	qt_retorno_w
	from 	prescr_procedimento b,
        	prescr_medica c,
       		exame_lab_resultado e,
        	exame_lab_result_item g,
		exame_laboratorio f
	where 	b.nr_prescricao         = c.nr_prescricao
       	and   	b.nr_prescricao         = e.nr_prescricao
	and   	e.nr_seq_resultado   = g.nr_seq_resultado
--	and   	g.nr_seq_exame       = f.nr_seq_exame
	and   	b.nr_seq_exame       = f.nr_seq_exame
	and     g.nr_seq_prescr        = b.nr_sequencia
       	and   	b.nr_prescricao         = nr_prescricao_p
       	and   	b.nr_sequencia         = nr_sequencia_p;


end if;

return	qt_retorno_w;

end;
$body$
