CREATE OR REPLACE FUNCTION obter_qtd_exame_lote_hemo (nr_seq_lote_p bigint) RETURNS bigint AS $body$
DECLARE


qtd_w	bigint;

BEGIN

SELECT 	COUNT(*)
into STRICT	qtd_w
FROM san_exame g, san_exame_realizado d, san_lote_hemoterapia_item b, san_lote_hemoterapia a, san_exame_lote c
LEFT OUTER JOIN san_doacao e ON (c.nr_seq_doacao = e.nr_sequencia)
LEFT OUTER JOIN san_tipo_doacao f ON (e.nr_seq_tipo = f.nr_sequencia)
WHERE a.nr_sequencia 		= b.nr_Seq_lote AND c.nr_sequencia 		= b.nr_seq_exame_lote AND d.nr_seq_exame_lote 	= c.nr_sequencia AND b.nr_seq_exame		= d.nr_seq_exame AND b.nr_seq_lote 		=  nr_seq_lote_p AND d.nr_seq_exame 		= g.nr_sequencia AND b.nr_seq_exame_lote 	= d.nr_seq_exame_lote;

return	qtd_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
