SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qtd_exame_prescricao (nr_prescricao_p bigint, ie_status_p bigint default 35) RETURNS varchar AS $body$
DECLARE


qtd_retorno_w		varchar(20);


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	SELECT   count(*) qtd
	into STRICT qtd_retorno_w
	FROM    result_laboratorio a,
		prescr_procedimento b, 
		prescr_medica c 
	WHERE     a.nr_prescricao      = nr_prescricao_p
	AND       a.nr_prescricao      = b.nr_prescricao 
	AND       a.nr_prescricao      = c.nr_prescricao 
        AND       a.nr_seq_prescricao  = b.nr_sequencia 
        AND       c.nr_prescricao      = a.nr_prescricao 
        AND       b.ie_status_atend    >= ie_status_p;
end if;

RETURN qtd_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
