CREATE OR REPLACE FUNCTION obter_qtd_hist_nao_lido_agenda (nr_seq_agenda_p bigint, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


qt_nao_lido_w	bigint:=0;
ie_conta_hist_w	varchar(1);


BEGIN

ie_conta_hist_w := Obter_Param_Usuario(871, 301, obter_perfil_ativo, nm_usuario_p, 0, ie_conta_hist_w);

if (ie_conta_hist_w = 'S') and (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
	select 	count(*)
	into STRICT	qt_nao_lido_w
	from 	agenda_pac_hist a
	where	a.nr_seq_agenda = nr_seq_agenda_p
	and	not exists (	SELECT	1
				from	agenda_pac_hist_leitura b
				where	a.nr_sequencia	= b.nr_seq_historico
				and	b.nm_usuario_leitura = nm_usuario_p);
end if;

return qt_nao_lido_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
