CREATE OR REPLACE FUNCTION obter_qtd_hist_os_tipo ( NR_SEQUENCIA_OS_P bigint, NR_SEQUENCIA_HIS_P bigint, DT_CORTE_P timestamp DEFAULT clock_timestamp() + interval '1 days') RETURNS varchar AS $body$
DECLARE

 PR_RETORNO_W	     bigint := 0;

BEGIN
	BEGIN
    SELECT COUNT(1) QTD
      INTO STRICT PR_RETORNO_W
      FROM MAN_ORDEM_SERV_TECNICO HIS
     WHERE HIS.NR_SEQ_ORDEM_SERV    = NR_SEQUENCIA_OS_P
       AND HIS.NR_SEQ_TIPO          = NR_SEQUENCIA_HIS_P
       AND coalesce(HIS.DT_INATIVACAO::text, '') = ''
       AND TRUNC(HIS.DT_HISTORICO) <= DT_CORTE_P;
	EXCEPTION
		WHEN no_data_found THEN
			PR_RETORNO_W := 0;
		WHEN OTHERS THEN
			PR_RETORNO_W := 0;
	END;

RETURN	PR_RETORNO_W;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
