SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qtd_info_adic (cd_doenca_p text, nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_info_adic_w	bigint;		
	    

BEGIN
	if (pkg_i18n.get_user_locale = 'de_DE') then
		select 	count(1)
		into STRICT	qt_info_adic_w
		from 	diag_doenca_inf_adic
		where 	nr_seq_diag_doenca in (SELECT a.nr_seq_interno
				 from 	diagnostico_doenca a,
						cid_doenca b
				 where a.cd_doenca = b.cd_doenca_cid
				 and a.cd_doenca = cd_doenca_p
				 and obter_pessoa_atendimento(a.nr_atendimento, 'C') = obter_pessoa_atendimento(nr_atendimento_p, 'C'))
				 and (coalesce(ie_revisado::text, '') = '' or ie_revisado = 'N' or ie_revisado = 'R')
		and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
		and coalesce(dt_inativacao::text, '') = '';
	else
		qt_info_adic_w := 0;
	end if;

return	qt_info_adic_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
