CREATE OR REPLACE FUNCTION obter_qtd_item_processo_adep (nr_prescricao_p bigint, nr_seq_material_p bigint, nr_seq_horario_p bigint) RETURNS bigint AS $body$
DECLARE

 
qt_item_w	double precision;


BEGIN 
 
if (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') then 
 
	select	(obter_dados_horario(nr_seq_horario_p,'QT'))::numeric  
	into STRICT	qt_item_w 
	;
 
elsif (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then 
 
	select	(obter_dados_material_prescr(nr_prescricao_p,nr_seq_material_p,'QT_UNITARIA'))::numeric  
	into STRICT	qt_item_w 
	;
 
end if;
 
return qt_item_w;
 
end;
