CREATE OR REPLACE FUNCTION obter_qtd_niveis_regra_pre_cad (ie_tipo_pre_cadastro_p text, cd_tipo_pessoa_fisica_p text, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE



nm_campo_w	varchar(100) := null;														
sql_w	varchar(1000) := null;
retorno_w 	smallint := null;

c_nome1 CURSOR FOR
SELECT * from processo_avaliacao 
where ie_tipo_processo = ie_tipo_pre_cadastro_p 
and   cd_tipo_pessoa = 1;


BEGIN							

	if (ie_tipo_pre_cadastro_p = 'FPF' or ie_tipo_pre_cadastro_p = 'FPJ') then
		select count(*)
		into STRICT retorno_w
			from processo_avaliacao pa 
			where coalesce(pa.cd_tipo_pessoa, 0) = coalesce(cd_tipo_pessoa_fisica_p, 0) 
			and pa.ie_tipo_processo = ie_tipo_pre_cadastro_p;
	elsif (ie_tipo_pre_cadastro_p = 'MAT') then
		select count(*)
			into STRICT retorno_w
			from processo_avaliacao pa
			where pa.ie_tipo_processo = ie_tipo_pre_cadastro_p;
	end if;
	
		
	return retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
