CREATE OR REPLACE FUNCTION obter_qtd_proc_ficha_tecnica ( nr_seq_proc_interno_p bigint, nr_seq_exame_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_estabelecimento_p bigint, cd_centro_controle_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*	ie_opcao_p
	F - Quantidade fixa
	V - Quantidade vari√°vel		*/
vl_retorno_w		double precision;
qt_fixa_w			double precision;
qt_variavel_w		double precision;
nr_ficha_tecnica_w		bigint;
nr_seq_componente_w	bigint;


BEGIN

begin
select	x.nr_ficha_tecnica,
	x.nr_seq_componente
into STRICT	nr_ficha_tecnica_w,
	nr_seq_componente_w
from (SELECT	1 ie_prioridade,
		b.nr_ficha_tecnica,
		b.nr_seq_componente
	from	ficha_tecnica_componente b,
		ficha_tecnica a
	where	a.nr_ficha_tecnica		= b.nr_ficha_tecnica
	and	a.ie_situacao		= 'A'
	and	b.ie_situacao		= 'A'
	and	a.cd_estabelecimento	= cd_estabelecimento_p
	and	b.cd_estabelecimento	= cd_estabelecimento_p
	and	b.cd_centro_controle	= cd_centro_controle_p
	and	a.nr_seq_proc_interno	= nr_seq_proc_interno_p
	
union all

	SELECT	2 ie_prioridade,
		b.nr_ficha_tecnica,
		b.nr_seq_componente
	from	ficha_tecnica_componente b,
		ficha_tecnica a
	where	a.nr_ficha_tecnica		= b.nr_ficha_tecnica
	and	a.ie_situacao		= 'A'
	and	b.ie_situacao		= 'A'
	and	a.cd_estabelecimento	= cd_estabelecimento_p
	and	b.cd_estabelecimento	= cd_estabelecimento_p
	and	b.cd_centro_controle	= cd_centro_controle_p
	and	a.nr_seq_exame		= nr_seq_exame_p
	
union all

	select	3 ie_prioridade,
		b.nr_ficha_tecnica,
		b.nr_seq_componente
	from	ficha_tecnica_componente b,
		ficha_tecnica a
	where	a.nr_ficha_tecnica		= b.nr_ficha_tecnica
	and	a.ie_situacao		= 'A'
	and	b.ie_situacao		= 'A'
	and	a.cd_estabelecimento	= cd_estabelecimento_p
	and	b.cd_estabelecimento	= cd_estabelecimento_p
	and	b.cd_centro_controle	= cd_centro_controle_p
	and	a.cd_procedimento		= cd_procedimento_p
	and	a.ie_origem_proced		= ie_origem_proced_p
	order by 1, 2 desc, 3 desc) x LIMIT 1;
exception
when others then
	nr_ficha_tecnica_w	:= 0;
	nr_seq_componente_w	:= 0;
end;


select	coalesce(max(qt_fixa),0),
	coalesce(max(qt_variavel),0)
into STRICT	qt_fixa_w,
	qt_variavel_w
from	ficha_tecnica_componente
where	nr_ficha_tecnica		= nr_ficha_tecnica_w
and	nr_seq_componente	= nr_seq_componente_w;

if (ie_opcao_p = 'F') then
	vl_retorno_w := qt_fixa_w;
elsif (ie_opcao_p = 'V') then
	vl_retorno_w := qt_variavel_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
