SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qtd_remessa_guia ( nr_atendimento_p bigint, cd_autorizacao_p text, ie_regra_remessa_p text) RETURNS bigint AS $body$
DECLARE

 
qt_remessa_w			bigint;

/* Regras de remessa N-Normal Z-Come√ßando no zero(menos um) */
 
 

BEGIN 
 
qt_remessa_w	:= 0;
 
select count(*) 
into STRICT	qt_remessa_w 
from	conta_paciente a, 
	conta_paciente_guia b 
where	a.nr_interno_conta	= b.nr_interno_conta 
and	b.nr_atendimento	= nr_atendimento_p 
and	b.cd_autorizacao	= cd_autorizacao_p 
and	coalesce(a.ie_cancelamento::text, '') = '' 
and	a.ie_status_acerto	= 2 
and	(a.nr_seq_protocolo IS NOT NULL AND a.nr_seq_protocolo::text <> '');
 
if (ie_regra_remessa_p	= 'Z') then 
	qt_remessa_w	:= qt_remessa_w - 1;
end if;
 
if (coalesce(qt_remessa_w::text, '') = '') 	or (qt_remessa_w < 0)		then 
	qt_remessa_w	:= 0;
end if;
	 
 
RETURN qt_remessa_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
