CREATE OR REPLACE FUNCTION obter_qtd_semana_dominio (dt_referencia_inicial_p timestamp, vl_dominio_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1) 	:= 'N';
i 		smallint   	:=0;
dt_dia_w 	timestamp 		:= trunc(dt_referencia_inicial_p,'month');
ie_dia_sem_w	varchar(10);


BEGIN

while(dt_dia_w <= last_day(trunc(dt_referencia_inicial_p,'dd'))) loop
	ie_dia_sem_w	:= obter_cod_dia_semana(dt_dia_w);
/*	if	(philips_param_pck.get_nr_seq_idioma = 2) then -- Espanhol  - MÃ©xico(MX)
		if	(ie_dia_sem_w = 7) then
			ie_dia_sem_w := 1;
		else
			ie_dia_sem_w	:= ie_dia_sem_w + 1;
		end if;
	end if;
*/
	if (ie_dia_sem_w = 1) then
		i := i +1;
	end if;
	dt_dia_w:= trunc(dt_dia_w) +1;
end loop;

if (vl_dominio_p <= i) then
	ie_retorno_w := 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
