CREATE OR REPLACE FUNCTION obter_qtd_transfusao_reserva ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


qtd_w	bigint;

BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	if (ie_opcao_p = 'T') then
		SELECT 	COUNT(*)
		into STRICT	qtd_w
		FROM   	san_doacao
		WHERE  	nr_seq_transfusao = nr_sequencia_p;
	else
		SELECT 	COUNT(*)
		into STRICT	qtd_w
		FROM   	san_doacao
		WHERE  	nr_seq_reserva = nr_sequencia_p;
	end if;
end if;

return	qtd_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
