CREATE OR REPLACE FUNCTION obter_qtd_visitantes_atend (nr_atendimento_p bigint, nm_usuario_p text, qt_param_30_ocup_p bigint default null) RETURNS bigint AS $body$
DECLARE

 
qt_visitante_w		bigint:= 0;
qt_dias_w		varchar(255);
cd_estabelecimento_w	smallint;


BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
/*	select	obter_valor_param_usuario(44, 30, obter_perfil_ativo, nm_usuario_p, obter_estab_atend(nr_atendimento_p)) 
	into	qt_dias_w 
	from	dual;*/
 
	select	cd_estabelecimento 
	into STRICT	cd_estabelecimento_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
 
	if (coalesce(qt_param_30_ocup_p::text, '') = '') then 
		qt_dias_w := Obter_Param_Usuario(44, 30, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, qt_dias_w);
	else 
		qt_dias_w := qt_param_30_ocup_p;
	end if;
 
	if ((qt_dias_w)::numeric  > 999999) then 
		qt_dias_w:= '999999';
	end if;
 
	select	count(*) 
	into STRICT	qt_visitante_w 
	from	atendimento_visita b 
	where	b.nr_atendimento = nr_atendimento_p 
	and	coalesce(b.dt_saida::text, '') = '' 
	and	dt_entrada between(clock_timestamp() - (qt_dias_w)::numeric ) and clock_timestamp() 
	and	coalesce(ie_paciente,'N') = 'N';
end if;
 
return	qt_visitante_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
