CREATE OR REPLACE FUNCTION obter_qtd_visitas_evolucao (nr_atendimento_p bigint, ie_tipo_evolucao_p text) RETURNS bigint AS $body$
DECLARE

			 
qt_resultado_w		bigint := 0;			
qt_reg_dia_w		bigint := 0;

C01 CURSOR FOR 
	SELECT 	count(*) 
	from	evolucao_paciente b, 
		atendimento_paciente a 
	where	b.nr_atendimento = nr_atendimento_p 
	and	a.nr_atendimento = b.nr_atendimento 
	and	b.ie_evolucao_clinica = ie_tipo_evolucao_p 
	and	b.dt_evolucao > a.dt_entrada 
	group by trunc(b.dt_evolucao);			
			 

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	qt_reg_dia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	if (qt_reg_dia_w > 0) then 
		qt_resultado_w := qt_resultado_w + 1;
	end if;
	 
	end;
end loop;
close C01;
 
return	qt_resultado_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
