CREATE OR REPLACE FUNCTION obter_qtexames_medicsolic (cd_medico_p text, cd_setor_atendimento_P bigint, ie_opcao_p text, dt_inicial_p timestamp, dt_final_p timestamp) RETURNS bigint AS $body$
DECLARE

 
/* 
ie_opcao_p 
 S - SUS 
 O - OUTROS 
*/
 
 
qt_total_w	bigint;


BEGIN 
 
if (ie_opcao_p = 'S') then 
	 
	begin 
	select	count(b.nr_prescricao) 
	into STRICT	qt_total_w 
	from	atendimento_paciente	c, 
		prescr_procedimento 	b, 
		prescr_medica 		a 
	where	a.nr_prescricao		= b.nr_prescricao 
	and	a.nr_atendimento	= c.nr_atendimento 
	and	b.cd_setor_atendimento	= cd_setor_atendimento_p 
	and	a.cd_medico 		= cd_medico_p 
	and	c.ie_tipo_convenio 	= 3 
	and	a.dt_prescricao 	between dt_inicial_p and dt_final_p;
	end;
 
elsif (ie_opcao_p = 'O') then	 
	 
	begin 
	select	count(b.nr_prescricao) 
	into STRICT	qt_total_w 
	from	atendimento_paciente	c, 
		prescr_procedimento	b, 
		prescr_medica		a 
	where	a.nr_prescricao		= b.nr_prescricao 
	and	a.nr_atendimento	= c.nr_atendimento 
	and	b.cd_setor_atendimento	= cd_setor_atendimento_p 
	and	a.cd_medico		= cd_medico_p 
	and	c.ie_tipo_convenio 	<> 3 
	and	a.dt_prescricao 	between dt_inicial_p and dt_final_p;
	end;
 
end if;
 
return qt_total_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
