CREATE OR REPLACE FUNCTION obter_qte_protocolo_lote (nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE

 
qt_protocolo_w		bigint;
				

BEGIN 
begin 
 
	select	count(*) 
	into STRICT	qt_protocolo_w 
	from 	protocolo_convenio a, 
			lote_protocolo   b 
	where 	a.nr_seq_lote_protocolo = b.nr_sequencia 
	and 	b.nr_sequencia = nr_sequencia_p 
	order by a.nr_seq_protocolo;
	 
exception 
when others then 
qt_protocolo_w		:= 0;
end;
 
return	qt_protocolo_w;
 
end;
