SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_antimicrobiano_inter ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_prescrito_w	bigint	:= 0;
qt_total_w	bigint	:= 0;
ds_retorno_w	varchar(255);


BEGIN

select	count(	distinct(c.cd_material))
into STRICT	qt_prescrito_w
from	material c,
	prescr_material b,
	prescr_medica a
where	a.nr_atendimento	= nr_atendimento_p
and	a.nr_prescricao		= b.nr_prescricao
and	b.cd_material		= c.cd_material
and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
and	coalesce(c.ie_controle_medico,0) <> 0;

select	count(	distinct(c.cd_material))
into STRICT	qt_total_w
from	material c,
	material_atend_paciente a
where	a.nr_atendimento	= nr_atendimento_p
and	a.cd_material		= c.cd_material
and	coalesce(c.ie_controle_medico,0) <> 0;

ds_retorno_w	:= wheb_mensagem_pck.get_texto(309499) || ': '|| to_char(qt_prescrito_w) ||' ' || wheb_mensagem_pck.get_texto(309500) || ': '||to_char(qt_total_w); -- Prescrito	-- Total
return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_qt_antimicrobiano_inter ( nr_atendimento_p bigint) FROM PUBLIC;

