SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_atend_diarias (dt_inicial_p timestamp, dt_final_p timestamp, cd_setor_atendimento_p bigint, ie_tipo_p text) RETURNS bigint AS $body$
DECLARE


/* Função criada especificamente para as OS 16867 e 31063*/

/* IE_TIPO_P
 S - SUS (16867)
 G - Geral (31063)
*/
dt_aux_w	timestamp;
qt_pacientes_w	bigint := 0;
qt_diarias_w	bigint := 0;


BEGIN

dt_aux_w	:=	trunc(dt_inicial_p);

while(dt_aux_w <= dt_final_p) loop
		begin

		if (cd_setor_atendimento_p	<> 0) then
			select  sum(1)
			into STRICT	qt_pacientes_w
			from    setor_atendimento s,
				atend_paciente_unidade a
			where (trunc(a.dt_saida_interno))	>= trunc(dt_aux_w)
			AND (trunc(a.dt_entrada_unidade))	<= trunc(dt_aux_w)
			AND     a.cd_setor_atendimento		= cd_setor_atendimento_p
			and     a.cd_setor_atendimento 		= s.cd_setor_atendimento
			AND (s.cd_classif_setor		in (3,4,8));

		elsif (cd_setor_atendimento_p	= 0) then
			if (ie_tipo_p	= 'S') then
				select  sum(1)
				into STRICT	qt_pacientes_w
				from    setor_atendimento s,
					atend_paciente_unidade a
				where (trunc(a.dt_saida_interno))	>= trunc(dt_aux_w)
				and (trunc(a.dt_entrada_unidade))	<= trunc(dt_aux_w)
				and     a.cd_setor_atendimento		= cd_setor_atendimento_p
				and     a.cd_setor_atendimento 		= s.cd_setor_atendimento
				and (s.cd_classif_setor		in (3,4,8));

			elsif (ie_tipo_p	= 'G') then
				select  sum(1)
				into STRICT	qt_pacientes_w
				from    setor_atendimento s,
					atend_paciente_unidade a
				where (trunc(a.dt_saida_interno))	>= trunc(dt_aux_w)
				and (trunc(a.dt_entrada_unidade))	<= trunc(dt_aux_w)
				and     a.cd_setor_atendimento		in (7,10,21)
				and     a.cd_setor_atendimento 		= s.cd_setor_atendimento
				and (s.cd_classif_setor		in (3,4,8));
			end if;
		end if;

		qt_diarias_w	:= qt_diarias_w + qt_pacientes_w;
		dt_aux_w	:= dt_aux_w + 1;

		end;
end loop;

RETURN	qt_diarias_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
