SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_atend_equipe_sos (nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


qt_atend_equipe_w	integer := 0;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select count(*)
	into STRICT qt_atend_equipe_w
	from EME_REG_RESPOSTA a
	where a.nr_seq_equipe = nr_sequencia_p
	and	(a.dt_chegada_base IS NOT NULL AND a.dt_chegada_base::text <> '')
	and	a.dt_chegada_base > clock_timestamp() - interval '1 days'
	and	exists ( SELECT	1
				from	EME_EQUIPE b
				where 	a.nr_seq_equipe = b.nr_seq_equipe
				and 	a.DT_CHEGADA_BASE between b.dt_referencia and coalesce(b.dt_referencia_fim,clock_timestamp())
				and 	clock_timestamp() between b.dt_referencia and coalesce(b.dt_referencia_fim,clock_timestamp())
				and		coalesce(a.DT_ACIONAMENTO,a.DT_PARTIDA) between b.dt_referencia and coalesce(b.dt_referencia_fim,clock_timestamp()));

end if;

return	qt_atend_equipe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
