SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_atraso_senha ( nr_seq_fila_espera_p bigint, ie_opcao_p text, nr_sequencia_p bigint default 0, dt_data_atraso_p timestamp default null) RETURNS bigint AS $body$
DECLARE


/*ie_opcao_p
MAX - tempo limite maximo
MIN - tempo limite minimo
*/
qt_diferenca_w			double precision;
qt_tempo_limite_max_w	numeric(20);
qt_tempo_limite_min_w	numeric(20);
ie_regra_tempo_espera_w	varchar(1);



BEGIN

select  coalesce(max(Obter_Valor_Param_Usuario(10021, 77, Obter_Perfil_Ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento)), 'S')
into STRICT	ie_regra_tempo_espera_w
;

select	coalesce(max(qt_tempo_maximo),0),
	coalesce(max(qt_tempo_medio),0)
into STRICT	qt_tempo_limite_max_w,
	qt_tempo_limite_min_w
from 	fila_espera_senha
where	nr_sequencia = nr_seq_fila_espera_p;

if (ie_opcao_p = 'MAX') then
	begin
	SELECT /*+INDEX (a PACSEFI_FILESPS_FK_I) */ max(((coalesce(dt_data_atraso_p,clock_timestamp()) - CASE WHEN ie_regra_tempo_espera_w='F' THEN  coalesce(dt_entrada_fila, dt_geracao_senha)  ELSE dt_geracao_senha END ) * 1440) - qt_tempo_limite_max_w)
	into STRICT	qt_diferenca_w
	FROM 	paciente_senha_fila a
	WHERE (coalesce(ie_rechamada,'N') = 'N')
	AND	coalesce(dt_utilizacao::text, '') = ''
	AND	coalesce(dt_inutilizacao::text, '') = ''
	AND	coalesce(dt_inicio_atendimento::text, '') = ''
	AND	coalesce(dt_fim_atendimento::text, '') = ''
	AND	coalesce(dt_entrada_fila, dt_geracao_senha) > clock_timestamp() - interval '2 days'
	and 	nr_seq_fila_senha = nr_seq_fila_espera_p
	and 	((nr_sequencia_p = 0) or (nr_sequencia = nr_sequencia_p))
	ORDER BY dt_geracao_senha DESC;
	end;

elsif (ie_opcao_p = 'MIN') then
	begin
	SELECT /*+INDEX (a PACSEFI_FILESPS_FK_I) */ max(((coalesce(dt_data_atraso_p,clock_timestamp()) - CASE WHEN ie_regra_tempo_espera_w='F' THEN  coalesce(dt_entrada_fila, dt_geracao_senha)  ELSE dt_geracao_senha END ) * 1440) - qt_tempo_limite_min_w)
	into STRICT	qt_diferenca_w
	FROM 	paciente_senha_fila a
	WHERE (coalesce(ie_rechamada,'N') = 'N')
	AND	coalesce(dt_utilizacao::text, '') = ''
	AND	coalesce(dt_inutilizacao::text, '') = ''
	AND	coalesce(dt_inicio_atendimento::text, '') = ''
	AND	coalesce(dt_fim_atendimento::text, '') = ''
	AND	coalesce(dt_entrada_fila, dt_geracao_senha) > clock_timestamp() - interval '2 days'
	and 	nr_seq_fila_senha = nr_seq_fila_espera_p
	and 	((nr_sequencia_p = 0) or (nr_sequencia = nr_sequencia_p))
	ORDER BY dt_geracao_senha DESC;
	end;
end if;

if (qt_diferenca_w < 0) then
	qt_diferenca_w	:= 0;
end if;

return	qt_diferenca_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_qt_atraso_senha ( nr_seq_fila_espera_p bigint, ie_opcao_p text, nr_sequencia_p bigint default 0, dt_data_atraso_p timestamp default null) FROM PUBLIC;

