CREATE OR REPLACE FUNCTION obter_qt_autconv_atend_agecons (nr_atendimento_p bigint, nr_seq_agecons_p bigint) RETURNS bigint AS $body$
DECLARE


qt_autorizada_w		integer	:= 0;


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_seq_agecons_p IS NOT NULL AND nr_seq_agecons_p::text <> '') then
	begin
	select	coalesce(sum(b.qt_autorizada),0)
	into STRICT	qt_autorizada_w
	from	procedimento_autorizado b,
		autorizacao_convenio a
	where	a.nr_sequencia				= b.nr_sequencia_autor
	and	substr(obter_estagio_autor(nr_seq_estagio,'C'),1,2)	= '10'
	and	a.nr_seq_agenda_consulta		= nr_seq_agecons_p
	and	a.nr_atendimento			= nr_atendimento_p;
	end;
end if;

return	qt_autorizada_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
