CREATE OR REPLACE FUNCTION obter_qt_autor_conv_solic_vaga ( nr_seq_solic_vaga_p bigint, nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

			
qt_retorno_w		bigint;
qt_autor_solic_W	bigint;

BEGIN

select	count(1)
into STRICT	qt_autor_solic_w
from	autorizacao_convenio
where	nr_seq_gestao = nr_seq_solic_vaga_p;

if (coalesce(qt_autor_solic_w,0) > 0) then

	select 	max(qt_dia_autorizado)
	into STRICT 	qt_retorno_w
	from 	autorizacao_convenio
	where	nr_seq_gestao = nr_seq_solic_vaga_p;

elsif (coalesce(nr_atendimento_p,0) > 0) then
	
	SELECT 	max(qt_dia_autorizado)
	into STRICT 	qt_retorno_w
	FROM 	autorizacao_convenio
	WHERE 	nr_atendimento = nr_atendimento_p 
	AND 	nr_sequencia = (SELECT MAX(nr_sequencia) 
				FROM autorizacao_convenio 
				WHERE nr_atendimento = nr_atendimento_p);

end if;
	
return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
