SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_aut_vigencia ( nr_seq_agend_origem_p bigint, nr_seq_agend_destino_p bigint ) RETURNS bigint AS $body$
DECLARE


			
qt_retorno_w		bigint;
dt_agendamento_w	timestamp;
			

BEGIN

select	max(ESTABLISHMENT_TIMEZONE_UTILS.DATEATTIME(dt_agenda, hr_inicio))
into STRICT	dt_agendamento_w
from	agenda_paciente
where	nr_sequencia = nr_seq_agend_destino_p;

select	count(*)
into STRICT	qt_retorno_w
from	autorizacao_convenio
where	nr_seq_agenda 	= nr_seq_agend_origem_p
and	(dt_retorno IS NOT NULL AND dt_retorno::text <> '')
and	dt_fim_vigencia < dt_agendamento_w;


return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
