CREATE OR REPLACE FUNCTION obter_qt_avaliacao_fornec ( cd_cgc_p text, dt_mesano_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_retorno_w	double precision;
qt_nota_w	double precision;
vl_nota_w	double precision;



BEGIN
select 	count(vl_media)
into STRICT	qt_nota_w
from	AVF_RESULTADO
where	cd_cnpj = cd_cgc_p
and	vl_media <> 0
and	trunc(DT_AVALIACAO,'mm') = dt_mesano_referencia_p;

select 	sum(vl_media)
into STRICT	vl_nota_w
from	AVF_RESULTADO
where	cd_cnpj = cd_cgc_p
and	vl_media <> 0
and	trunc(DT_AVALIACAO,'mm') = dt_mesano_referencia_p;

	vl_retorno_w :=  dividir(vl_nota_w,qt_nota_w);

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
