SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_cirurgia ( cd_local_estoque_p bigint, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


qt_cirurgia_w		double precision;


BEGIN

if (cd_local_estoque_p IS NOT NULL AND cd_local_estoque_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	begin
	/*Matheus OS 98295 Retirei o indice deste SQL */

	select	coalesce(sum(qt_total_dispensar),0)
	into STRICT	qt_cirurgia_w
	from	setor_atendimento c,
		cirurgia b,
		material x,
		prescr_material a,
		prescr_medica m
	where	a.cd_motivo_baixa = 0
	and	a.cd_material		= x.cd_material
	and	x.cd_material_estoque	= cd_material_p
	and	m.nr_prescricao		= b.nr_prescricao
	and	m.nr_prescricao		= a.nr_prescricao
	and	b.cd_setor_atendimento	= c.cd_setor_atendimento
	and	c.cd_local_estoque		= cd_local_estoque_p
	and	a.ie_status_cirurgia		in ('CB','AD');
	end;
end if;

return	qt_cirurgia_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_qt_cirurgia ( cd_local_estoque_p bigint, cd_material_p bigint) FROM PUBLIC;

