CREATE OR REPLACE FUNCTION obter_qt_clear_creatinina (nr_atendimento_p atendimento_paciente.nr_atendimento%TYPE, cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%TYPE) RETURNS bigint AS $body$
DECLARE


	qt_cockcroft_gault_w	double precision;
	qt_schwartz_w			double precision;		
	qt_clearence_w			double precision;
	qt_anos_w				bigint;


BEGIN
	qt_clearence_w := 0;

	if ((coalesce(nr_atendimento_p,0) > 0) and (coalesce(cd_pessoa_fisica_p,'XPTO') <> 'XPTO')) then
		
		select	max((obter_idade(coalesce(dt_nascimento,clock_timestamp()),coalesce(dt_obito,clock_timestamp()),'A'))::numeric )
		into STRICT	qt_anos_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;	
		
		select	max(qt_cockcroft_gault),
				max(qt_schwartz)
		into STRICT	qt_cockcroft_gault_w,
				qt_schwartz_w
		from	pac_clereance_creatinina
		where	nr_sequencia	= (	SELECT	max(nr_sequencia)
									  from	pac_clereance_creatinina
									 where	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
									   and 	coalesce(ie_situacao,'A') = 'A'
									   and	nr_atendimento = nr_atendimento_p);

		qt_clearence_w := qt_schwartz_w;

		if (qt_anos_w >= 16) then
			qt_clearence_w := coalesce(qt_cockcroft_gault_w,0);
		end if;

	end if;

	return coalesce(qt_clearence_w, 0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
