CREATE OR REPLACE FUNCTION obter_qt_compra_mensal ( cd_estabelecimento_p bigint, dt_mesano_referencia_p timestamp, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


qt_compra_mensal_w		double precision;


BEGIN

select	coalesce(sum(c.qt_item_nf),0)
into STRICT	qt_compra_mensal_w
from	material a,
	nota_fiscal b,
	nota_fiscal_item c
where	a.cd_material  = c.cd_material
and	b.nr_sequencia = c.nr_sequencia
and	b.ie_situacao  = '1'
and	substr(obter_se_nota_entrada_saida(b.nr_sequencia),1,1) = 'E'
and	trunc(b.dt_entrada_saida, 'month') = trunc(dt_mesano_referencia_p, 'month')
and	b.cd_estabelecimento = cd_estabelecimento_p
and	a.cd_material = cd_material_p;

return qt_compra_mensal_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
