CREATE OR REPLACE FUNCTION obter_qt_conj_esterilizado ( nr_seq_conjunto_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w		bigint;


BEGIN

select	count(*)
into STRICT	ds_retorno_w
from	cm_conjunto_cont a
where	not exists (SELECT 1 from cm_requisicao_conj x where x.nr_seq_conj_real = a.nr_sequencia)
and 	a.ie_status_conjunto = 3
and	a.nr_seq_conjunto = nr_seq_conjunto_p
and	a.cd_estabelecimento = cd_estabelecimento_p
and	coalesce(a.ie_situacao,'A') = 'A'
and	coalesce(a.dt_validade,trunc(clock_timestamp())) >= trunc(clock_timestamp());

return	ds_retorno_w;

end;
