CREATE OR REPLACE FUNCTION obter_qt_convert_item_inspecao (nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


qt_prevista_entrega_w		double precision;


BEGIN

select	coalesce(CASE WHEN obter_dados_material(a.cd_material,'UME')=obter_dados_item_ordem(a.nr_ordem_compra, a.nr_item_oci,'UMC') THEN 		1  ELSE obter_conversao_material(a.cd_material, 'CE') END  * coalesce(a.qt_inspecao,0),0)
into STRICT	qt_prevista_entrega_w
from	inspecao_recebimento a
where	nr_sequencia = nr_sequencia_p;

return	qt_prevista_entrega_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
